import rsa
re_jie = """
-----BEGIN RSA PRIVATE KEY-----
MGICAQACEQCRqtdOPu4muaAT4iw3D1JzAgMBAAECEE1UzQiVEAkoIYlWfXK+EsEC
CQzLfAkQju5OBwIIC2KEWkdyvTUCCQv8kh4X4h+E/wIIBscBD1VVQrkCCQUkAN7a
aUs9fg==
-----END RSA PRIVATE KEY-----
"""
def decryption(crypto, re_jie):
    privkey = rsa.PrivateKey.load_pkcs1(re_jie)
    message = rsa.decrypt(crypto, privkey)
    return message

re_com = """
-----BEGIN RSA PUBLIC KEY-----
MBgCEQCRqtdOPu4muaAT4iw3D1JzAgMBAAE=
-----END RSA PUBLIC KEY-----
"""
def encryption(cmd, re_com):
    pubkey = rsa.PublicKey.load_pkcs1(re_com)
    return rsa.encrypt(cmd, pubkey)

